package gen

import (
	"github.com/jinzhu/gorm"
	"github.com/rs/zerolog/log"
	"gopkg.in/gormigrate.v1"
)

// Migrate runs migrations
func Migrate(db *gorm.DB, options *gormigrate.Options, migrations []*gormigrate.Migration) error {
	m := gormigrate.New(db, options, migrations)

	// // it's possible to use this, but in case of any specific keys or columns are created in migrations, they will not be generated by automigrate
	// m.InitSchema(func(tx *gorm.DB) error {
	// 	return AutoMigrate(db)
	// })

	return m.Migrate()
}

func AutoMigrate(db *gorm.DB) (err error) {
	_db := db.AutoMigrate(
		Delivery{},
		VehicleType{},
		PaymentForm{},
		Deliver{},
		Person{},
	)
	if _db.Error != nil {
		log.Err(_db.Error).Send()
		return _db.Error
	}
	if _db.Dialect().GetName() != "sqlite3" {

		err = _db.Model(Delivery{}).RemoveForeignKey("senderId", TableName("people")+"(id)").Error
		if err != nil {
			log.Err(err).Send()
		}
		err = _db.Model(Delivery{}).AddForeignKey("senderId", TableName("people")+"(id)", "SET NULL", "SET NULL").Error
		if err != nil {
			log.Err(err).Send()
		}

		err = _db.Model(Delivery{}).RemoveForeignKey("receiverId", TableName("people")+"(id)").Error
		if err != nil {
			log.Err(err).Send()
		}
		err = _db.Model(Delivery{}).AddForeignKey("receiverId", TableName("people")+"(id)", "SET NULL", "SET NULL").Error
		if err != nil {
			log.Err(err).Send()
		}

		err = _db.Model(Delivery{}).RemoveForeignKey("deliverId", TableName("delivers")+"(id)").Error
		if err != nil {
			log.Err(err).Send()
		}
		err = _db.Model(Delivery{}).AddForeignKey("deliverId", TableName("delivers")+"(id)", "SET NULL", "SET NULL").Error
		if err != nil {
			log.Err(err).Send()
		}

		err = _db.Model(Person{}).RemoveForeignKey("deliveriesSentId", TableName("deliveries")+"(id)").Error
		if err != nil {
			log.Err(err).Send()
		}
		err = _db.Model(Person{}).AddForeignKey("deliveriesSentId", TableName("deliveries")+"(id)", "SET NULL", "SET NULL").Error
		if err != nil {
			log.Err(err).Send()
		}

		err = _db.Model(Person{}).RemoveForeignKey("deliveriesReceivedId", TableName("deliveries")+"(id)").Error
		if err != nil {
			log.Err(err).Send()
		}
		err = _db.Model(Person{}).AddForeignKey("deliveriesReceivedId", TableName("deliveries")+"(id)", "SET NULL", "SET NULL").Error
		if err != nil {
			log.Err(err).Send()
		}

		if _db.Error != nil {
			log.Err(_db.Error).Send()
		}
	}
	return nil // _db.Error
}
